mapper.select:
SELECT t.areaID AS areaID, t.areaName AS areaName, t.areaCode AS areaCode, t.parentID AS parentID, t.areaLevel AS areaLevel, t.createTime AS createTime, t.status AS status FROM SYS_BASE_AREA t WHERE t.status = 1
AND t.areaLevel = #{areaLevel}
AND t.areaName like CONCAT('%', #{areaSearchName}, '%')
LIMIT #{startIndex}, #{endIndex}
